package NeosoftLab.java;

public class PattrnJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n = 5;  
	        for(int i = 5 ; i <= n ; i--)  
	        {  
	        for(int j = i-1 ; j < i ; j++)  
	        {  
	            System.out.print(j);  
	        }  
	            System.out.println("");  
	        }  

	}

}
